// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@zetachain/protocol-contracts/contracts/zevm/SystemContract.sol";
import "@zetachain/protocol-contracts/contracts/zevm/interfaces/IGatewayZEVM.sol";
import "@zetachain/protocol-contracts/contracts/zevm/interfaces/IWZETA.sol";
import "@zetachain/protocol-contracts/contracts/zevm/interfaces/IZRC20.sol";
import "@zetachain/protocol-contracts/contracts/Revert.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

interface IDragonSwapManager {
    function setPair(address market, address tokenA, address tokenB) external; // called by factory

    function rebalance(address market) external; // keeper or cron

    function unwindForPayout(
        address market,
        uint256 needed
    ) external returns (uint256 got);
}

contract DragonSwapManager is IDragonSwapManager, Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    SystemContract public systemContract;
    IGatewayZEVM public gateway;

    struct Market {
        address tokenA;
        address tokenB;
        uint256 reserveA;
        uint256 reserveB;
        uint256 totalLiquidity;
        bool isActive;
        uint256 lastRebalanceTime;
        uint256 targetRatio; // ratio of tokenA to tokenB (in basis points, 5000 = 50%)
    }

    struct CrossChainRequest {
        address sender;
        address market;
        uint256 amount;
        uint256 chainId;
        bytes data;
    }

    mapping(address => Market) public markets;
    mapping(address => bool) public authorizedFactories;
    mapping(address => bool) public authorizedKeepers;
    mapping(bytes32 => CrossChainRequest) public pendingRequests;

    address[] public marketList;

    uint256 public constant REBALANCE_THRESHOLD = 500; // 5% deviation
    uint256 public constant REBALANCE_COOLDOWN = 1 hours;
    uint256 public constant BASIS_POINTS = 10000;

    event MarketCreated(address indexed market, address tokenA, address tokenB);
    event MarketRebalanced(
        address indexed market,
        uint256 newReserveA,
        uint256 newReserveB
    );
    event LiquidityUnwound(
        address indexed market,
        uint256 amount,
        uint256 received
    );
    event CrossChainSwapInitiated(
        address indexed market,
        uint256 amount,
        uint256 targetChain
    );
    event CrossChainSwapCompleted(bytes32 indexed requestId, uint256 amount);

    modifier onlyAuthorizedFactory() {
        require(authorizedFactories[msg.sender], "Unauthorized factory");
        _;
    }

    modifier onlyAuthorizedKeeper() {
        require(
            authorizedKeepers[msg.sender] || msg.sender == owner(),
            "Unauthorized keeper"
        );
        _;
    }

    modifier validMarket(address market) {
        require(markets[market].isActive, "Market not active");
        _;
    }

    // Add constructor to initialize Ownable with initial owner
    constructor(address initialOwner) Ownable(initialOwner) {
        // You can add initialization logic here if needed
    }

    function setPair(
        address market,
        address tokenA,
        address tokenB
    ) external override onlyAuthorizedFactory {
        require(
            market != address(0) &&
                tokenA != address(0) &&
                tokenB != address(0),
            "Invalid addresses"
        );
        require(!markets[market].isActive, "Market already exists");

        markets[market] = Market({
            tokenA: tokenA,
            tokenB: tokenB,
            reserveA: 0,
            reserveB: 0,
            totalLiquidity: 0,
            isActive: true,
            lastRebalanceTime: block.timestamp,
            targetRatio: 5000 // 50% by default
        });

        marketList.push(market);

        emit MarketCreated(market, tokenA, tokenB);
    }

    function rebalance(
        address market
    ) external override onlyAuthorizedKeeper validMarket(market) {
        Market storage marketData = markets[market];

        require(
            block.timestamp >=
                marketData.lastRebalanceTime + REBALANCE_COOLDOWN,
            "Rebalance cooldown not met"
        );

        require(_needsRebalancing(market), "Rebalancing not needed");

        // Calculate target reserves based on current total value
        uint256 totalValue = _calculateTotalValue(market);
        uint256 targetValueA = (totalValue * marketData.targetRatio) /
            BASIS_POINTS;
        uint256 targetValueB = totalValue - targetValueA;

        // Convert to token amounts (simplified - in real implementation would use price oracles)
        uint256 targetReserveA = targetValueA;
        uint256 targetReserveB = targetValueB;

        // Perform the rebalancing
        _executeRebalance(market, targetReserveA, targetReserveB);

        marketData.lastRebalanceTime = block.timestamp;

        emit MarketRebalanced(market, marketData.reserveA, marketData.reserveB);
    }

    function unwindForPayout(
        address market,
        uint256 needed
    ) external override validMarket(market) nonReentrant returns (uint256 got) {
        Market storage marketData = markets[market];

        // Calculate how much liquidity to unwind
        uint256 totalValue = _calculateTotalValue(market);
        require(totalValue >= needed, "Insufficient liquidity");

        uint256 unwindRatio = (needed * BASIS_POINTS) / totalValue;

        uint256 unwindA = (marketData.reserveA * unwindRatio) / BASIS_POINTS;
        uint256 unwindB = (marketData.reserveB * unwindRatio) / BASIS_POINTS;

        // Transfer tokens to requester
        if (unwindA > 0) {
            IERC20(marketData.tokenA).safeTransfer(msg.sender, unwindA);
        }
        if (unwindB > 0) {
            IERC20(marketData.tokenB).safeTransfer(msg.sender, unwindB);
        }

        // Update reserves
        marketData.reserveA -= unwindA;
        marketData.reserveB -= unwindB;

        got = unwindA + unwindB;

        emit LiquidityUnwound(market, needed, got);

        return got;
    }

    // Cross-chain functionality using ZetaChain Gateway
    function initiateCrossChainSwap(
        address market,
        uint256 amount,
        uint256 targetChain,
        address targetAddress,
        bytes calldata callData
    ) external validMarket(market) {
        Market storage marketData = markets[market];

        // Transfer tokens from user
        IERC20(marketData.tokenA).safeTransferFrom(
            msg.sender,
            address(this),
            amount
        );

        // Create cross-chain request
        bytes32 requestId = keccak256(
            abi.encodePacked(msg.sender, market, amount, block.timestamp)
        );
        pendingRequests[requestId] = CrossChainRequest({
            sender: msg.sender,
            market: market,
            amount: amount,
            chainId: targetChain,
            data: callData
        });

        // Use ZetaChain Gateway for cross-chain call
        gateway.call(
            abi.encodePacked(targetAddress), // receiver as bytes
            marketData.tokenA, // zrc20 token for fees
            callData, // message
            CallOptions({gasLimit: 500000, isArbitraryCall: true}),
            RevertOptions({
                revertAddress: msg.sender,
                callOnRevert: true,
                abortAddress: address(0),
                revertMessage: abi.encode("refund"),
                onRevertGasLimit: 500_000
            })
        );

        emit CrossChainSwapInitiated(market, amount, targetChain);
    }

    // Handle incoming cross-chain calls
    function handleCrossChainCall(
        address sender,
        uint256 sourceChain,
        bytes calldata data
    ) external {
        // Only allow calls from the gateway
        require(msg.sender == address(gateway), "Only gateway can call");

        // Decode the cross-chain message
        (address market, uint256 amount, bytes memory swapData) = abi.decode(
            data,
            (address, uint256, bytes)
        );

        require(markets[market].isActive, "Invalid market");

        // Process the cross-chain swap
        _processCrossChainSwap(sender, market, amount, sourceChain, swapData);
    }

    // Internal functions
    function _needsRebalancing(address market) internal view returns (bool) {
        Market storage marketData = markets[market];

        if (marketData.reserveA == 0 || marketData.reserveB == 0) {
            return false;
        }

        uint256 totalValue = _calculateTotalValue(market);
        uint256 currentRatioA = (marketData.reserveA * BASIS_POINTS) /
            totalValue;
        uint256 deviation = currentRatioA > marketData.targetRatio
            ? currentRatioA - marketData.targetRatio
            : marketData.targetRatio - currentRatioA;

        return deviation > REBALANCE_THRESHOLD;
    }

    function _calculateTotalValue(
        address market
    ) internal view returns (uint256) {
        Market storage marketData = markets[market];
        return marketData.reserveA + marketData.reserveB;
    }

    function _executeRebalance(
        address market,
        uint256 targetReserveA,
        uint256 targetReserveB
    ) internal {
        Market storage marketData = markets[market];

        if (targetReserveA > marketData.reserveA) {
            uint256 swapAmount = targetReserveA - marketData.reserveA;
            if (swapAmount <= marketData.reserveB) {
                marketData.reserveA += swapAmount;
                marketData.reserveB -= swapAmount;
            }
        } else if (targetReserveB > marketData.reserveB) {
            uint256 swapAmount = targetReserveB - marketData.reserveB;
            if (swapAmount <= marketData.reserveA) {
                marketData.reserveB += swapAmount;
                marketData.reserveA -= swapAmount;
            }
        }
    }

    function _processCrossChainSwap(
        address sender,
        address market,
        uint256 amount,
        uint256 sourceChain,
        bytes memory swapData
    ) internal {
        bytes32 requestId = keccak256(
            abi.encodePacked(sender, market, amount, block.timestamp)
        );

        // Update market reserves based on the swap
        Market storage marketData = markets[market];
        marketData.reserveA += amount;

        emit CrossChainSwapCompleted(requestId, amount);
    }

    // Admin functions
    function setAuthorizedFactory(
        address factory,
        bool authorized
    ) external onlyOwner {
        authorizedFactories[factory] = authorized;
    }

    function setAuthorizedKeeper(
        address keeper,
        bool authorized
    ) external onlyOwner {
        authorizedKeepers[keeper] = authorized;
    }

    function setMarketTargetRatio(
        address market,
        uint256 ratio
    ) external onlyOwner validMarket(market) {
        require(ratio <= BASIS_POINTS, "Invalid ratio");
        markets[market].targetRatio = ratio;
    }

    function addLiquidity(
        address market,
        uint256 amountA,
        uint256 amountB
    ) external validMarket(market) nonReentrant {
        Market storage marketData = markets[market];

        IERC20(marketData.tokenA).safeTransferFrom(
            msg.sender,
            address(this),
            amountA
        );
        IERC20(marketData.tokenB).safeTransferFrom(
            msg.sender,
            address(this),
            amountB
        );

        marketData.reserveA += amountA;
        marketData.reserveB += amountB;
        marketData.totalLiquidity += amountA + amountB;
    }

    function emergencyWithdraw(
        address market
    ) external onlyOwner validMarket(market) {
        Market storage marketData = markets[market];

        if (marketData.reserveA > 0) {
            IERC20(marketData.tokenA).safeTransfer(
                owner(),
                marketData.reserveA
            );
        }
        if (marketData.reserveB > 0) {
            IERC20(marketData.tokenB).safeTransfer(
                owner(),
                marketData.reserveB
            );
        }

        marketData.reserveA = 0;
        marketData.reserveB = 0;
        marketData.isActive = false;
    }

    // View functions
    function getMarketInfo(
        address market
    ) external view returns (Market memory) {
        return markets[market];
    }

    function getAllMarkets() external view returns (address[] memory) {
        return marketList;
    }

    function getMarketCount() external view returns (uint256) {
        return marketList.length;
    }
}

contracts/Market.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

interface ICivicGate {
    function check(bytes calldata proof, address user) external view returns (bool);
}

contract Market is ReentrancyGuard {
    using SafeERC20 for IERC20;

    enum Side { YES, NO }

    string public question;
    uint64 public endTime;
    address public payoutToken;
    bytes32 public civicRule;
    address public creator;
    address public civicGate;
    address public dragonSwapManager;

    bool public isResolved;
    Side public winner;

    mapping(address => mapping(Side => uint256)) public userShares;
    mapping(address => uint256) public userDeposits;

    uint256 public yesShares;
    uint256 public noShares;
    uint256 public totalLiquidity;

    event Joined(
        address indexed user,
        Side side,
        uint256 amount,
        uint256 shares,
        address viaChain
    );

    event Resolved(Side winner, uint256 timestamp);

    event Redeemed(
        address indexed user,
        uint256 amount,
        uint256 totalShares
    );

    modifier notExpired() {
        require(block.timestamp <= endTime, "Market expired");
        _;
    }

    modifier onlyAfterExpiry() {
        require(block.timestamp > endTime, "Market not expired");
        _;
    }

    modifier notResolved() {
        require(!isResolved, "Market resolved");
        _;
    }

    modifier onlyResolved() {
        require(isResolved, "Market not resolved");
        _;
    }

    modifier onlyCreatorOrManager() {
        require(
            msg.sender == creator || msg.sender == dragonSwapManager,
            "Not authorized"
        );
        _;
    }

    constructor(
        string memory _question,
        uint64 _endTime,
        address _payoutToken,
        bytes32 _civicRule,
        address _civicGate,
        address _dragonSwapManager,
        address _creator
    ) {
        require(_endTime > block.timestamp, "Invalid end time");
        require(_payoutToken != address(0), "Invalid payout token");
        require(_creator != address(0), "Invalid creator");
        require(_dragonSwapManager != address(0), "Invalid manager");

        question = _question;
        endTime = _endTime;
        payoutToken = _payoutToken;
        civicRule = _civicRule;
        civicGate = _civicGate;
        dragonSwapManager = _dragonSwapManager;
        creator = _creator;
    }

    function join(
        Side side,
        uint256 amount,
        bytes calldata civicProof
    ) external notExpired notResolved nonReentrant {
        _join(msg.sender, side, amount, civicProof, address(0));
    }

    function joinFromZeta(
        Side side,
        uint256 amount,
        address user,
        bytes calldata civicProof
    ) external notExpired notResolved onlyCreatorOrManager nonReentrant {
        _join(user, side, amount, civicProof, msg.sender);
    }

    function _join(
        address user,
        Side side,
        uint256 amount,
        bytes calldata civicProof,
        address viaChain
    ) internal {
        require(amount > 0, "Invalid amount");
        require(user != address(0), "Invalid user");

        // Check Civic gate if required
        if (civicRule != bytes32(0) && civicGate != address(0)) {
            require(
                ICivicGate(civicGate).check(civicProof, user),
                "Civic verification failed"
            );
        }

        // Transfer tokens from user (or via chain caller)
        address payer = viaChain != address(0) ? viaChain : user;
        IERC20(payoutToken).safeTransferFrom(payer, address(this), amount);

        // Calculate shares (simplified constant product for demo)
        uint256 shares = amount; // 1:1 for simplicity

        if (side == Side.YES) {
            yesShares += shares;
        } else {
            noShares += shares;
        }

        userShares[user][side] += shares;
        userDeposits[user] += amount;
        totalLiquidity += amount;

        emit Joined(user, side, amount, shares, viaChain);
    }

    function resolve(Side _winner) 
        external 
        onlyAfterExpiry 
        notResolved 
        onlyCreatorOrManager 
    {
        isResolved = true;
        winner = _winner;

        emit Resolved(_winner, block.timestamp);
    }

    function redeem() external onlyResolved nonReentrant {
        uint256 winningShares = userShares[msg.sender][winner];
        require(winningShares > 0, "No winning shares");

        uint256 totalWinningShares = winner == Side.YES ? yesShares : noShares;
        require(totalWinningShares > 0, "No winning shares exist");

        uint256 payout = (winningShares * totalLiquidity) / totalWinningShares;

        // Clear user shares before transfer to prevent reentrancy
        userShares[msg.sender][winner] = 0;

        // Transfer payout using the payout token
        IERC20(payoutToken).safeTransfer(msg.sender, payout);

        emit Redeemed(msg.sender, payout, winningShares);
    }

    // Emergency function to unwind liquidity for payouts via DragonSwapManager
    function unwindLiquidity(uint256 amount) 
        external 
        onlyResolved 
        returns (uint256 unwound) 
    {
        require(msg.sender == dragonSwapManager, "Only manager can unwind");
        require(amount <= totalLiquidity, "Insufficient liquidity");

        totalLiquidity -= amount;
        IERC20(payoutToken).safeTransfer(dragonSwapManager, amount);

        return amount;
    }

    function getOdds() external view returns (uint256 yesPrice, uint256 noPrice) {
        uint256 total = yesShares + noShares;
        if (total == 0) {
            return (50, 50); // 50/50 if no shares
        }

        yesPrice = (yesShares * 100) / total;
        noPrice = 100 - yesPrice;
    }

    function getUserPosition(address user) 
        external 
        view 
        returns (uint256 yes, uint256 no, uint256 deposited) 
    {
        return (
            userShares[user][Side.YES],
            userShares[user][Side.NO],
            userDeposits[user]
        );
    }

    function getMarketInfo() 
        external 
        view 
        returns (
            string memory _question,
            uint64 _endTime,
            address _payoutToken,
            bool _isResolved,
            Side _winner,
            uint256 _yesShares,
            uint256 _noShares,
            uint256 _totalLiquidity
        ) 
    {
        return (
            question,
            endTime,
            payoutToken,
            isResolved,
            winner,
            yesShares,
            noShares,
            totalLiquidity
        );
    }

    // Emergency withdrawal function for creator
    function emergencyWithdraw() external {
        require(msg.sender == creator, "Only creator");
        require(block.timestamp > endTime + 30 days, "Too early for emergency");

        uint256 balance = IERC20(payoutToken).balanceOf(address(this));
        if (balance > 0) {
            IERC20(payoutToken).safeTransfer(creator, balance);
        }
    }
}

contracts/MarketFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./Market.sol";
import "./CivicGate.sol";
import "./DragonSwapManager.sol";

contract MarketFactory {
    address public civicGate;
    address public dragonSwapManager;
    address public owner;

    mapping(address => bool) public isMarket;
    address[] public markets;

    event MarketCreated(
        address indexed market,
        address indexed creator,
        string question,
        uint64 endTime,
        bytes32 civicRule,
        address payoutToken
    );

    modifier onlyOwner() {
        require(msg.sender == owner, "Not owner");
        _;
    }

    constructor(address _civicGate, address _dragonSwapManager) {
        civicGate = _civicGate;
        dragonSwapManager = _dragonSwapManager;
        owner = msg.sender;
    }

    function createMarket(
        string calldata question,
        uint64 endTime,
        address payoutToken,
        bytes32 civicRule,
        uint256 initialLiquidity
    ) external returns (address) {
        require(endTime > block.timestamp, "Invalid end time");
        require(initialLiquidity > 0, "Invalid liquidity");

        // Deploy new market
        Market market = new Market(
            question,
            endTime,
            payoutToken,
            civicRule,
            civicGate,
            dragonSwapManager,
            msg.sender
        );

        address marketAddress = address(market);
        isMarket[marketAddress] = true;
        markets.push(marketAddress);

        // Set up DragonSwap pair if manager is available
        if (dragonSwapManager != address(0)) {
            IDragonSwapManager(dragonSwapManager).setPair(
                marketAddress,
                payoutToken,
                marketAddress // Market contract acts as share token
            );
        }

        emit MarketCreated(
            marketAddress,
            msg.sender,
            question,
            endTime,
            civicRule,
            payoutToken
        );

        return marketAddress;
    }

    function getMarketsCount() external view returns (uint256) {
        return markets.length;
    }

    function getMarkets(uint256 offset, uint256 limit) 
        external 
        view 
        returns (address[] memory) 
    {
        require(offset < markets.length, "Offset out of bounds");

        uint256 end = offset + limit;
        if (end > markets.length) {
            end = markets.length;
        }

        address[] memory result = new address[](end - offset);
        for (uint256 i = offset; i < end; i++) {
            result[i - offset] = markets[i];
        }

        return result;
    }
}

